ASBEGINSET NOCOUNT ON;--------------------------------------------------------------------------------------	  -- SELECTIOn des INFOCOLLINSERT INTO ARTICLES_TI_VEILLE_PERSO (TITRE,LIEN,MAIL_CODE,CONTENU,ACTU_INFOCOLL,ID_SELLIGENT_CONTENU,DATE_CONTENU,ID_ARTICLE,AUTEUR,PDF_PRESENT,CONTENUS_A_ENVOYER,NOTE_EDITEUR,EDITOR_COMMENT_DATE)SELECT DISTINCT	ARTICLES_TI_CONTENU_INFOCOLL.TITRE,	(SELECT TOP 1 LIEN FROM ARTICLES_TI_CONTENU_INFOCOLL T1 WHERE T1.ID_ARTICLE = ARTICLES_TI_CONTENU_INFOCOLL.ID_ARTICLE) AS LIEN,	DATA_TI_THESAURUS_USERS.MAIL_CODE AS MAIL_CODE,	CONTENU,	1 AS ACTU_INFOCOLL,	(SELECT MAX(ID) FROM ARTICLES_TI_CONTENU_INFOCOLL T1 WHERE T1.ID_ARTICLE = ARTICLES_TI_CONTENU_INFOCOLL.ID_ARTICLE) AS ID_ARTICLE_SELLIGENT,	ARTICLES_TI_CONTENU_INFOCOLL.DATE_CONTENU,	ARTICLES_TI_CONTENU_INFOCOLL.ID_ARTICLE,	ARTICLES_TI_CONTENU_INFOCOLL.AUTEUR,	ARTICLES_TI_CONTENU_INFOCOLL.PDF_PRESENT,	1,
	ARTICLES_TI_CONTENU_INFOCOLL.NOTE_EDITEUR,
	ARTICLES_TI_CONTENU_INFOCOLL.EDITOR_COMMENT_DATEFROM ARTICLES_TI_CONTENU_INFOCOLLLEFT JOIN DATA_TI_THESAURUS_CONTENU ON ARTICLES_TI_CONTENU_INFOCOLL.ID = DATA_TI_THESAURUS_CONTENU.ID_ARTICLE_SELLIGENT AND ORIGIN_ARTICLE=0LEFT JOIN DATA_TI_THESAURUS_USERS ON DATA_TI_THESAURUS_USERS.TAG_ID = DATA_TI_THESAURUS_CONTENU.TAG_IDWHERE DATA_TI_THESAURUS_USERS.TAG_ID is nOT NULLAND ARTICLES_TI_CONTENU_INFOCOLL.STATUT IN (3,4)AND ARTICLES_TI_CONTENU_INFOCOLL.DATE_CONTENU > GETDATE()-8;END